@import url("https://fonts.googleapis.com/css2?family=Tajawal:wght@300;400;500;700&display=swap");
* { margin: 0px; padding: 0px; box-sizing: border-box; }
body { font-family: Tajawal, Cairo, sans-serif; line-height: 1.6; color: rgb(51, 51, 51); background: linear-gradient(135deg, rgb(102, 126, 234) 0%, rgb(118, 75, 162) 100%); min-height: 100vh; direction: rtl; }
.App { min-height: 100vh; display: flex; flex-direction: column; }
.header { background: rgba(255, 255, 255, 0.1); backdrop-filter: blur(10px); border-bottom: 1px solid rgba(255, 255, 255, 0.2); padding: 1rem 0px; }
.header-content { max-width: 1200px; margin: 0px auto; padding: 0px 20px; display: flex; justify-content: space-between; align-items: center; }
.logo { font-size: 2rem; font-weight: bold; color: white; text-shadow: rgba(0, 0, 0, 0.3) 2px 2px 4px; }
.navigation ul { list-style: none; display: flex; gap: 2rem; }
.navigation a { color: white; text-decoration: none; font-weight: 500; transition: 0.3s; }
.navigation a:hover { color: rgb(255, 215, 0); transform: translateY(-2px); }
.main-content { flex: 1 1 0%; display: flex; align-items: center; justify-content: center; padding: 2rem; }
.container { width: 100%; max-width: 1200px; margin: 0px auto; }
.login-container { max-width: 400px; margin: 0px auto; background: rgba(255, 255, 255, 0.95); border-radius: 20px; box-shadow: rgba(0, 0, 0, 0.3) 0px 20px 60px; overflow: hidden; animation: 0.5s ease 0s 1 normal none running slideIn; }
@keyframes slideIn { 
  0% { opacity: 0; transform: translateY(30px); }
  100% { opacity: 1; transform: translateY(0px); }
}
.login-header { background: linear-gradient(135deg, rgb(102, 126, 234) 0%, rgb(118, 75, 162) 100%); color: white; padding: 2rem; text-align: center; }
.login-header h2 { margin-bottom: 0.5rem; font-size: 1.8rem; }
.login-header p { opacity: 0.9; font-size: 0.95rem; }
.login-form { padding: 2rem; }
.form-group { margin-bottom: 1.5rem; }
.form-group label { display: block; margin-bottom: 0.5rem; color: rgb(85, 85, 85); font-weight: 600; }
.form-group input { width: 100%; padding: 0.75rem; border: 2px solid rgb(224, 224, 224); border-radius: 10px; font-size: 1rem; transition: 0.3s; font-family: inherit; direction: rtl; }
.form-group input:focus { outline: none; border-color: rgb(102, 126, 234); box-shadow: rgba(102, 126, 234, 0.1) 0px 0px 0px 3px; }
.login-button { width: 100%; padding: 1rem; background: linear-gradient(135deg, rgb(102, 126, 234) 0%, rgb(118, 75, 162) 100%); color: white; border: none; border-radius: 10px; font-size: 1.1rem; font-weight: 600; cursor: pointer; transition: 0.3s; font-family: inherit; }
.login-button:hover { transform: translateY(-2px); box-shadow: rgba(102, 126, 234, 0.4) 0px 10px 30px; }
.login-button:active { transform: translateY(0px); }
.footer { background: rgba(0, 0, 0, 0.2); backdrop-filter: blur(10px); padding: 1.5rem 0px; margin-top: auto; }
.footer-content { max-width: 1200px; margin: 0px auto; padding: 0px 20px; text-align: center; color: white; }
.footer-content p { opacity: 0.9; }
@media (max-width: 768px) {
  .header-content { flex-direction: column; gap: 1rem; }
  .navigation ul { flex-direction: column; text-align: center; gap: 1rem; }
  .App { padding: 0px 10px; }
}
@media (max-width: 480px) {
  body { font-size: 14px; }
  .login-container { margin: 0px 10px; }
  .login-header { padding: 1.5rem; }
  .login-form { padding: 1.5rem; }
  .footer { padding: 1rem 0px; }
  .footer-content { font-size: 0.9rem; }
}
