.upload-label { display: flex; flex-direction: column; align-items: center; justify-content: center; padding: 32px; background: linear-gradient(145deg, rgb(250, 250, 250) 0%, rgb(242, 242, 247) 100%); border: 2px dashed rgb(229, 229, 234); border-radius: 16px; cursor: pointer; transition: 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94); text-align: center; }
.upload-label:hover { border-color: rgb(0, 122, 255); background: linear-gradient(145deg, rgb(255, 255, 255) 0%, rgb(248, 248, 252) 100%); transform: translateY(-2px); box-shadow: rgba(0, 122, 255, 0.1) 0px 4px 16px; }
.upload-icon { width: 48px; height: 48px; color: rgb(142, 142, 147); margin-bottom: 12px; transition: 0.3s; }
.upload-label:hover .upload-icon { color: rgb(0, 122, 255); transform: scale(1.1); }
.upload-text { font-size: 16px; font-weight: 500; color: rgb(28, 28, 30); margin-bottom: 4px; }
.upload-hint { font-size: 12px; color: rgb(142, 142, 147); }
.upload-success { position: absolute; top: 16px; right: 16px; display: flex; align-items: center; gap: 6px; color: rgb(52, 199, 89); font-size: 14px; font-weight: 500; background: rgba(52, 199, 89, 0.1); padding: 6px 12px; border-radius: 100px; animation: 0.3s ease 0s 1 normal none running slideInDown; }
.family-members-section { margin-top: 32px; padding: 24px; background: linear-gradient(145deg, rgb(250, 250, 250) 0%, rgb(247, 247, 252) 100%); border-radius: 16px; border: 1px solid rgb(229, 229, 234); }
.subsection-title { font-size: 18px; font-weight: 600; color: rgb(28, 28, 30); margin: 0px 0px 20px; display: flex; align-items: center; gap: 8px; }
.family-members-grid { display: grid; grid-template-columns: repeat(auto-fill, minmax(300px, 1fr)); gap: 16px; }
.family-member-card { background: white; border-radius: 12px; padding: 16px; border: 1px solid rgb(229, 229, 234); transition: 0.3s; }
.family-member-card:hover { box-shadow: rgba(0, 0, 0, 0.06) 0px 4px 12px; transform: translateY(-1px); }
.member-header { display: flex; align-items: center; gap: 8px; font-size: 14px; font-weight: 500; color: rgb(28, 28, 30); margin-bottom: 12px; padding-bottom: 12px; border-bottom: 1px solid rgb(229, 229, 234); }
.member-fields { display: flex; flex-direction: column; gap: 12px; }
.checkbox-group { margin-bottom: 24px; }
.checkbox-label { display: flex; align-items: center; cursor: pointer; padding: 16px; background: linear-gradient(145deg, rgb(250, 250, 250) 0%, rgb(247, 247, 252) 100%); border-radius: 12px; border: 1px solid rgb(229, 229, 234); transition: 0.3s; }
.checkbox-label:hover { background: linear-gradient(145deg, rgb(255, 255, 255) 0%, rgb(248, 248, 252) 100%); border-color: rgb(0, 122, 255); }
.checkbox-input { width: 22px; height: 22px; margin-left: 16px; cursor: pointer; accent-color: rgb(0, 122, 255); }
.checkbox-text { display: flex; align-items: center; gap: 8px; font-size: 16px; font-weight: 500; color: rgb(28, 28, 30); }
.conditional-field { margin-top: 16px; animation: 0.3s ease 0s 1 normal none running slideInDown; }
.summary-section { margin-top: 32px; padding: 24px; background: linear-gradient(135deg, rgba(0, 122, 255, 0.05) 0%, rgba(88, 86, 214, 0.05) 100%); border-radius: 16px; border: 1px solid rgba(0, 122, 255, 0.2); }
.summary-title { display: flex; align-items: center; gap: 8px; font-size: 18px; font-weight: 600; color: rgb(0, 122, 255); margin-bottom: 20px; }
.summary-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 16px; }
.summary-item { display: flex; justify-content: space-between; padding: 12px; background: white; border-radius: 8px; }
.summary-label { font-size: 14px; color: rgb(142, 142, 147); font-weight: 500; }
.summary-value { font-size: 14px; color: rgb(28, 28, 30); font-weight: 600; }
.form-navigation { display: flex; justify-content: space-between; align-items: center; margin-top: 40px; padding-top: 32px; border-top: 1px solid rgb(229, 229, 234); }
.primary-button { background: linear-gradient(135deg, rgb(0, 122, 255) 0%, rgb(0, 86, 204) 100%); color: white; border: none; border-radius: 16px; padding: 16px 32px; font-size: 16px; font-weight: 600; cursor: pointer; transition: 0.2s cubic-bezier(0.25, 0.46, 0.45, 0.94); box-shadow: rgba(0, 122, 255, 0.3) 0px 4px 16px; display: flex; align-items: center; gap: 8px; margin-right: auto; }
.primary-button:hover { transform: translateY(-1px) scale(1.02); box-shadow: rgba(0, 122, 255, 0.4) 0px 8px 24px; }
.primary-button:active { transform: translateY(0px) scale(0.98); }
.primary-button.loading { opacity: 0.8; cursor: not-allowed; }
.secondary-button { background: linear-gradient(145deg, rgb(255, 255, 255) 0%, rgb(242, 242, 247) 100%); color: rgb(0, 122, 255); border: 1.5px solid rgb(0, 122, 255); border-radius: 16px; padding: 16px 32px; font-size: 16px; font-weight: 600; cursor: pointer; transition: 0.2s cubic-bezier(0.25, 0.46, 0.45, 0.94); }
.secondary-button:hover { background: linear-gradient(145deg, rgb(0, 122, 255) 0%, rgb(0, 86, 204) 100%); color: white; transform: translateY(-1px); box-shadow: rgba(0, 122, 255, 0.3) 0px 4px 16px; }
.button-icon { width: 20px; height: 20px; }
.button-spinner { width: 20px; height: 20px; border-width: 2px; border-style: solid; border-color: white rgba(255, 255, 255, 0.3) rgba(255, 255, 255, 0.3); border-image: initial; border-radius: 50%; animation: 1s linear 0s infinite normal none running spin; }
.error-banner { background: linear-gradient(145deg, rgb(255, 245, 245) 0%, rgb(255, 235, 235) 100%); border: 1px solid rgb(255, 59, 48); border-radius: 16px; padding: 16px 20px; margin-bottom: 24px; display: flex; align-items: flex-start; gap: 12px; }
.error-icon { width: 24px; height: 24px; color: rgb(255, 59, 48); flex-shrink: 0; }
.error-messages { flex: 1 1 0%; }
.error-message { font-size: 14px; color: rgb(255, 59, 48); font-weight: 500; margin: 0px 0px 4px; }
.error-message:last-child { margin-bottom: 0px; }
.success-screen { max-width: 600px; margin: 100px auto; padding: 60px; background: linear-gradient(145deg, rgb(255, 255, 255) 0%, rgb(251, 251, 253) 100%); border-radius: 32px; box-shadow: rgba(0, 0, 0, 0.04) 0px 4px 12px, rgba(0, 0, 0, 0.08) 0px 16px 32px, rgba(0, 0, 0, 0.12) 0px 32px 64px; text-align: center; }
.success-icon-wrapper { position: relative; width: 120px; height: 120px; margin: 0px auto 32px; }
.success-icon-bg { position: absolute; inset: 0px; background: linear-gradient(135deg, rgb(52, 199, 89) 0%, rgb(48, 179, 80) 100%); border-radius: 50%; animation: 2s ease-in-out 0s infinite normal none running pulseSuccess; }
.success-icon { position: absolute; inset: 20%; width: 60%; height: 60%; color: white; z-index: 1; animation: 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94) 0.3s 1 normal backwards running checkmarkScale; }
.success-title { font-size: 32px; font-weight: 700; color: rgb(28, 28, 30); margin: 0px 0px 16px; letter-spacing: -0.5px; }
.success-message { font-size: 18px; color: rgb(142, 142, 147); line-height: 1.6; margin: 0px 0px 32px; }
.success-details { background: linear-gradient(145deg, rgb(250, 250, 250) 0%, rgb(242, 242, 247) 100%); border-radius: 16px; padding: 24px; margin-bottom: 32px; }
.detail-item { display: flex; justify-content: space-between; padding: 12px 0px; }
.detail-item:not(:last-child) { border-bottom: 1px solid rgb(229, 229, 234); }
.detail-label { font-size: 14px; color: rgb(142, 142, 147); font-weight: 500; }
.detail-value { font-size: 14px; color: rgb(28, 28, 30); font-weight: 600; font-family: "SF Mono", Monaco, monospace; }
.success-actions { display: flex; justify-content: center; gap: 16px; }
.map-preview { margin-top: 24px; }
.map-placeholder { height: 200px; background: linear-gradient(145deg, rgb(250, 250, 250) 0%, rgb(242, 242, 247) 100%); border-radius: 16px; border: 1px solid rgb(229, 229, 234); display: flex; flex-direction: column; align-items: center; justify-content: center; color: rgb(142, 142, 147); }
@keyframes shimmer { 
  0%, 100% { transform: translateX(-100%) rotate(0deg); opacity: 0.5; }
  50% { transform: translateX(100%) rotate(180deg); opacity: 0.8; }
}
@keyframes particleFloat { 
  0% { transform: translateY(0px) translateX(0px); }
  100% { transform: translateY(-100vh) translateX(-100px); }
}
@keyframes breathe { 
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}
@keyframes sparkle { 
  0%, 100% { opacity: 1; transform: scale(1) rotate(0deg); }
  50% { opacity: 0.8; transform: scale(1.1) rotate(180deg); }
}
@keyframes slideInUp { 
  0% { opacity: 0; transform: translateY(30px); }
  100% { opacity: 1; transform: translateY(0px); }
}
@keyframes slideInDown { 
  0% { opacity: 0; transform: translateY(-20px); }
  100% { opacity: 1; transform: translateY(0px); }
}
@keyframes lineGlow { 
  0%, 100% { opacity: 0; }
  50% { opacity: 1; }
}
@keyframes spin { 
  100% { transform: rotate(360deg); }
}
@keyframes pulseSuccess { 
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.1); opacity: 0.8; }
}
@keyframes checkmarkScale { 
  0% { transform: scale(0) rotate(-45deg); }
  100% { transform: scale(1) rotate(0deg); }
}
@keyframes animate-shake { 
  0%, 100% { transform: translateX(0px); }
  10%, 30%, 50%, 70%, 90% { transform: translateX(-2px); }
  20%, 40%, 60%, 80% { transform: translateX(2px); }
}
.animate-slide-in { animation: 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s 1 normal backwards running slideInUp; }
.animate-scale-in { animation: 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s 1 normal backwards running checkmarkScale; }
.animate-shake { animation: 0.5s ease 0s 1 normal none running animate-shake; }
@media (max-width: 768px) {
  .premium-header { padding: 40px 20px 30px; }
  .header-content { flex-direction: column; gap: 24px; text-align: center; }
  .logo-section { flex-direction: column; }
  .header-info { width: 100%; justify-content: center; }
  .info-item { font-size: 12px; padding: 8px 16px; }
  .step-indicator { flex-direction: column; gap: 16px; }
  .step-line { width: 2px; height: 40px; }
  .form-container { padding: 20px; }
  .form-section { padding: 24px 16px; border-radius: 20px; }
  .form-grid { grid-template-columns: 1fr; gap: 20px; }
  .upload-grid { grid-template-columns: 1fr; }
  .family-members-grid { grid-template-columns: 1fr; }
  .form-navigation { flex-direction: column-reverse; gap: 16px; }
  .primary-button, .secondary-button { width: 100%; justify-content: center; }
  .primary-button { margin-right: 0px; }
  .success-screen { margin: 40px 20px; padding: 40px 24px; }
}
@media (max-width: 480px) {
  .logo-title { font-size: 24px; }
  .logo-subtitle { font-size: 14px; }
  .section-title { font-size: 24px; }
  .section-subtitle { font-size: 14px; }
  .input-field { height: 52px; font-size: 15px; }
  .step-icon { width: 48px; height: 48px; }
  .step-label { font-size: 12px; }
}
@media (prefers-reduced-motion: reduce) {
  *, ::before, ::after { animation-duration: 0.01ms !important; animation-iteration-count: 1 !important; transition-duration: 0.01ms !important; }
}
@media (prefers-color-scheme: dark) {
}
.form-section, .step-item, .upload-label, .primary-button, .secondary-button { will-change: transform; transform: translateZ(0px); }
:focus-visible { outline: rgb(0, 122, 255) solid 2px; outline-offset: 2px; }
@media (prefers-contrast: high) {
  .input-field { border-width: 2px; }
  .primary-button { border: 2px solid transparent; }
  .error-banner { border-width: 2px; }
}
@media print {
  .premium-header, .form-navigation, .step-indicator-container { display: none; }
  .form-section { box-shadow: none; break-inside: avoid; }
}
[dir="rtl"] .button-icon { transform: rotate(180deg); }
[dir="rtl"] select.input-field { background-position: right 16px center; padding-right: 20px; padding-left: 50px; }
